import React from 'react';
import { Link } from 'react-router-dom';

function Nav() {
  // 로컬 스토리지에서 토큰을 확인하여 로그인 상태 결정
  const isLoggedIn = localStorage.getItem('token') ? true : false;

  const handleLogout = () => {
    // 로컬 스토리지에서 토큰 삭제
    localStorage.removeItem('token');
    // 로그아웃 후 페이지를 새로고침하거나, 사용자를 홈페이지로 리다이렉트할 수 있습니다.
    window.location.href = '/';
  };

  return (
      <nav>
        <svg width="50" height="50" viewBox="0 0 1024 1024" fill="none"
             xmlns="http://www.w3.org/2000/svg">
          <path
              d="M512 109.09L912 384v448a64 64 0 01-64 64H704a32 32 0 01-32-32V608a32 32 0 00-32-32H384a32 32 0 00-32 32v256a32 32 0 01-32 32H176a64 64 0 01-64-64V384L512 109.09z"
              fill="#6C63FF"/>
          <path d="M328 928V616a8 8 0 018-8h352a8 8 0 018 8v312"
                stroke="#4D4D4D" stroke-width="72"/>
        </svg>
        <h1>모두의 TODO</h1>
        <h3>할일을 자유롭게 입력하세요!</h3>
      </nav>
  );
}

export default Nav;
