import React, { useEffect, useState } from 'react';
import {useNavigate, useParams} from 'react-router-dom';

function ArticleDetail() {
  const { id } = useParams();
  const [article, setArticle] = useState(null);
  const navigate = useNavigate(); // useNav
  useEffect(() => {
    const fetchArticle = async () => {
      // 로컬 스토리지에서 토큰을 가져옵니다.
      const token = localStorage.getItem('token');

      // 요청 시 인증 토큰을 Authorization 헤더에 포함합니다.
      const response = await fetch(`/api/articles/${id}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (!response.ok) {
        // 요청 실패 처리 (예: 에러 메시지 설정)
        console.error("Failed to fetch article details.");
        return;
      }

      const data = await response.json();
      setArticle(data);
    };
    fetchArticle();
  }, [id]);

  if (!article) return <div>Loading...</div>;
  const handleArticleClick = (articleId) => {
    navigate(`/board/articles/${articleId}`); // 게시글 상세보기 페이지로 이동
  };
  return (
      <div className="article-detail-page">
        <h4>작성자: {article.username}</h4>
        <h3>제목: {article.title}</h3>
        <div className="text-box">
          <p>글내용: {article.content}</p>
        </div>
        <button onClick={() => handleArticleClick(article.id)}>글 수정하기
        </button>
      </div>
  );
}

export default ArticleDetail;
