import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

function ArticleDetail() {
  const { id } = useParams();
  const [article, setArticle] = useState(null);
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    fetchArticleAndComments();
  }, [id]);

  const fetchArticleAndComments = async () => {
    // 게시글 상세 정보 가져오기
    fetchArticle();
    // 댓글 목록 가져오기
    fetchComments();
  };

  const fetchArticle = async () => {
    // 이전 구현과 동일
  };

  const fetchComments = async () => {
    const token = localStorage.getItem('token');
    const response = await fetch(`/api/articles/${id}/comments`, {
      headers: {
        'Authorization': `Bearer ${token}`
      }
    });

    if (!response.ok) {
      console.error("Failed to fetch comments.");
      return;
    }

    const data = await response.json();
    setComments(data);
  };

  const handleNewCommentChange = (e) => {
    setNewComment(e.target.value);
  };

  const submitComment = async () => {
    const token = localStorage.getItem('token');
    const response = await fetch(`/api/articles/${id}/comments`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`
      },
      body: JSON.stringify({ content: newComment })
    });

    if (!response.ok) {
      console.error("Failed to submit comment.");
      return;
    }

    setNewComment('');
    fetchComments(); // 댓글 목록 새로고침
  };

  if (!article) return <div>Loading...</div>;

  return (
      <div className="article-detail-page">
        {/* 게시글 정보 표시 */}
        <h4>작성자: {article.username}</h4>
        <h3>제목: {article.title}</h3>
        <div className="text-box">
          <p>글내용: {article.content}</p>
        </div>
        <button onClick={() => navigate(`/board/editArticles/${id}`)}>글 수정하기</button>
        <button onClick={() => navigate(`/board/deleteArticles/${id}`)}>글 삭제하기</button>

        {/* 댓글 목록 표시 */}
        <div className="comments-section">
          <h4>댓글</h4>
          {comments.map((comment) => (
              <div key={comment.id} className="comment">
                <p>{comment.content}</p>
              </div>
          ))}
        </div>

        {/* 댓글 작성 폼 */}
        <div className="comment-form">
          <textarea value={newComment} onChange={handleNewCommentChange} placeholder="댓글을 입력하세요..." />
          <button onClick={submitComment}>댓글 작성</button>
        </div>
      </div>
  );
}

export default ArticleDetail;
