import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function Nav() {
  // 간단한 예제를 위한 로그인 상태 (실제 앱에서는 인증 상태를 관리하는 로직 필요)
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogout = () => {
    // 로그아웃 처리 로직 (예: 토큰 삭제)
    setIsLoggedIn(false);
    // 여기에 로그아웃을 처리하는 코드를 추가하세요. 예: localStorage에서 토큰 삭제
  };

  return (
      <nav>
        <h1>모두의 TODO</h1>
        <div>
          {!isLoggedIn ? (
              <>
                <Link to="/login">로그인</Link>
                <Link to="/signup">회원가입</Link>
              </>
          ) : (
              <button onClick={handleLogout}>로그아웃</button>
          )}
        </div>
      </nav>
  );
}

export default Nav;
