import React, { useEffect, useState } from 'react';

function AllArticles() {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(false); // 로딩 상태 추가
  const [error, setError] = useState(null); // 에러 상태 추가
  const navigate = useNavigate(); // useNavigate 훅을 사용하여 navigate 함수를 초기화합니다.

  useEffect(() => {
    fetchArticles();
  }, []);

  const fetchArticles = async () => {
    setLoading(true);
    try {
      // 로컬 스토리지에서 토큰을 가져옵니다.
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('Authentication token not found');
      }

      // 요청 시 인증 토큰을 Authorization 헤더에 포함합니다.
      const response = await fetch('/api/articles', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (!response.ok) {
        throw new Error('Something went wrong');
      }

      const data = await response.json();
      setArticles(data);
      console.log(data)
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  // 로딩 중일 때 표시할 UI
  if (loading) {
    return <div>Loading...</div>;
  }

  // 에러가 발생했을 때 표시할 UI
  if (error) {
    return <div>Error: {error}</div>;
  }
  // 게시글 카드 클릭 핸들러
  const handleArticleClick = (articleId) => {
    navigate(`/articles/${articleId}`); // 게시글 상세보기 페이지로 이동
  };
  return (
      <div>
        <h2>게시글</h2>
        {articles.map(article => (
            <div className="article-card" key={article.id} onClick={() => handleArticleClick(article.id)}>
              <h4>작성자: {article.username}</h4>
              <h3>제목: {article.title}</h3>
              <p>내용: {article.content}</p>
            </div>
        ))}
      </div>
  );
}

export default AllArticles;
