import React from 'react';
import { Link } from 'react-router-dom';

function Nav() {
  // 로컬 스토리지에서 토큰을 확인하여 로그인 상태 결정
  const isLoggedIn = localStorage.getItem('token') ? true : false;

  const handleLogout = () => {
    // 로컬 스토리지에서 토큰 삭제
    localStorage.removeItem('token');
    // 로그아웃 후 페이지를 새로고침하거나, 사용자를 홈페이지로 리다이렉트할 수 있습니다.
    window.location.href = '/';
  };

  return (
      <nav>
        <h1>모두의 TODO</h1>
        <div>
          {isLoggedIn ? (
              <button className="App-link" onClick={handleLogout}>로그아웃</button>
          ) : (
              <>
                <Link to="/login" style={{marginRight: '10px'}}><button className="App-link">로그인</button></Link>
                <Link to="/signup"><button className="App-link">회원가입</button></Link>
              </>
          )}
        </div>
      </nav>
  );
}

export default Nav;
