import React from 'react';
import {Routes, Route, useNavigate} from 'react-router-dom';
import AllArticles from "./allArticle";
import AddArticle from "./addArticles";
import ArticleDetail from "./articleDetail";
function parseJwt(token) {
  // JWT 토큰의 페이로드 부분만 추출합니다.
  const base64Url = token.split('.')[1];
  // base64Url을 base64로 변환합니다.
  const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
  // base64 인코딩된 문자열을 디코딩합니다.
  const jsonPayload = decodeURIComponent(atob(base64).split('').map(function(c) {
    return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
  }).join(''));

  // JSON 문자열을 객체로 변환합니다.
  return JSON.parse(jsonPayload);
};

// 사용 예시
const token = '여기에.JWT.토큰을.넣으세요';
const userInfo = parseJwt(token);

console.log(userInfo); // 사용자 정보 출력

function Board() {
  // useNavigate 훅을 사용하여 navigate 함수를 생성합니다.
  const navigate = useNavigate();

  return (
      <div>
        <nav>
          <ul>
            <li>
              {/* onClick 이벤트 핸들러에서 navigate 함수를 호출합니다. */}
              <button onClick={() => navigate('/board/addArticles')}>아티클 추가
              </button>
              <button onClick={() => navigate('/board/articles')}>아티클 조회
              </button>
            </li>
          </ul>
        </nav>

        {/* 라우트 설정 */}
        // Board 컴포넌트 내
        <Routes>
          <Route path="articles" element={<AllArticles />} />
          <Route path="articles/:id" element={<ArticleDetail />} />
          <Route path="addArticles" element={<AddArticle />} />
        </Routes>
      </div>
  );
}

export default Board;
